import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Loading from "./Loading";
import axios from "axios";
import { useFormik } from "formik";
import * as Yup from "yup";

const Home = ({ setResult }) => {
  const formik = useFormik({
    initialValues: {
      fullName: "",
      currentPosition: "",
      currentLength: "",
      currentTechnologies: "",
      companyName: "",
      position: "",
    },
  });

  const [fullName, setFullName] = useState("");
  const [currentPosition, setCurrentPosition] = useState("");
  const [currentLength, setCurrentLength] = useState(1);
  const [currentTechnologies, setCurrentTechnologies] = useState("");
  const [headshot, setHeadshot] = useState(null);
  const [companyInfo, setCompanyInfo] = useState([{ name: "", position: "" }]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleAddCompany = () =>
    setCompanyInfo([...companyInfo, { name: "", position: "" }]);

  const handleRemoveCompany = (index) => {
    const list = [...companyInfo];
    list.splice(index, 1);
    setCompanyInfo(list);
  };
  const handleUpdateCompany = (e, index) => {
    const { name, value } = e.target;
    const list = [...companyInfo];
    list[index][name] = value;
    setCompanyInfo(list);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log(formik.values);

    const formData = new FormData();
    formData.append("headshotImage", headshot, headshot.name);
    formData.append("fullName", fullName);
    formData.append("currentPosition", currentPosition);
    formData.append("currentLength", currentLength);
    formData.append("currentTechnologies", currentTechnologies);
    formData.append("workHistory", JSON.stringify(companyInfo));
    axios
      .post("http://localhost:4000/resume/create", formData, {})
      .then((res) => {
        if (res.data.message) {
          setResult(res.data.data);
          navigate("/resume");
        }
      })
      .catch((err) => console.error(err));
    setLoading(true);
  };
  if (loading) {
    return <Loading />;
  }
  return (
    <main className="h-screen flex flex-col items-center justify-center">
      <div className="bg-white flex flex-col items-center rounded-lg w-1/2 font-latoRegular">
        <h1 className="text-4xl font-bold items-center underline p-3">
          Resume Builder
        </h1>
        <p>Generate a resume with ChatGPT in few seconds</p>
        <form
          onSubmit={handleFormSubmit}
          method="POST"
          encType="multipart/form-data"
        >
          <label className="text-lg font-bold text-gray-900" htmlFor="fullName">
            Full Name
          </label>
          <input
            className="border-2 border-gray-500 p-2 rounded-md w-full focus:border-teal-500 focus:ring-teal-500"
            type="text"
            required
            name="fullName"
            id="fullName"
            value={formik.values.fullName}
            // onChange={(e) => setFullName(e.target.value)}
            onChange={formik.handleChange}
            placeholder="enter your full name"
          />
          <div>
            <div>
              <label
                className="text-lg font-bold text-gray-900"
                htmlFor="currentPosition"
              >
                Current Position
              </label>
              <input
                className="border-2 border-gray-500 p-2 rounded-md w-full focus:border-teal-500 focus:ring-teal-500"
                type="text"
                required
                name="currentPosition"
                value={formik.values.currentPosition}
                onChange={formik.handleChange}
                // onChange={(e) => setCurrentPosition(e.target.value)}
                placeholder="enter your current position"
              />
            </div>
            <div>
              <label
                className="text-lg font-bold text-gray-900"
                htmlFor="currentLength"
              >
                For how long? (year)
              </label>
              <input
                className="border-2 border-gray-500 p-2 rounded-md w-full focus:border-teal-500 focus:ring-teal-500"
                type="text"
                required
                name="currentLength"
                // className="currentInput"
                value={formik.values.currentLength}
                onChange={formik.handleChange}
                // onChange={(e) => setCurrentLength(e.target.value)}
                placeholder="enter your experience for how long ?"
              />
            </div>
            <div>
              <label
                className="text-lg font-bold text-gray-900"
                htmlFor="currentTechnologies"
              >
                Technologies used
              </label>
              <input
                className="border-2 border-gray-500 p-2 rounded-md w-full focus:border-teal-500 focus:ring-teal-500"
                type="text"
                required
                name="currentTechnologies"
                // className="currentInput"
                value={formik.values.currentTechnologies}
                // onChange={(e) => setCurrentTechnologies(e.target.value)}
                onChange={formik.handleChange}
                placeholder="enter technologies used"
              />
            </div>
          </div>
          <label className="text-sm font-bold text-gray-900" htmlFor="photo">
            Upload your headshot image
          </label>
          <input
            className="border-2 border-gray-500 p-2 rounded-md w-full focus:border-teal-500 focus:ring-teal-500"
            type="file"
            name="photo"
            required
            id="photo"
            accept="image/x-png,image/jpeg"
            // value={formik.values.photo}
            onChange={(e) => setHeadshot(e.target.files[0])}
          />

          <h3 className="text-lg font-bold text-gray-900">
            Companies you've worked at
          </h3>

          {companyInfo.map((company, index) => (
            <div className="nestedContainer" key={index}>
              <div className="companies">
                <label
                  className="text-lg font-bold text-gray-900"
                  htmlFor="name"
                >
                  Company Name
                </label>
                <input
                  className="border-2 border-gray-500 p-2 rounded-md w-full focus:border-teal-500 focus:ring-teal-500"
                  type="text"
                  name="companyName"
                  required
                  value={formik.values.companyName}
                  //   onChange={(e) => handleUpdateCompany(e, index)}
                  onChange={formik.handleChange}
                  placeholder="enter company name"
                />
              </div>
              <div className="companies">
                <label
                  className="text-lg font-bold text-gray-900"
                  htmlFor="position"
                >
                  Position Held
                </label>
                <input
                  className="border-2 border-gray-500 p-2 rounded-md w-full focus:border-teal-500 focus:ring-teal-500"
                  type="text"
                  name="position"
                  required
                  value={formik.values.position}
                  //   onChange={(e) => handleUpdateCompany(e, index)}
                  onChange={formik.handleChange}
                  placeholder="enter position"
                />
              </div>

              <div className="btn__group">
                {companyInfo.length - 1 === index && companyInfo.length < 4 && (
                  <button id="addBtn" onClick={handleAddCompany}>
                    Add
                  </button>
                )}
                {companyInfo.length > 1 && (
                  <button
                    id="deleteBtn"
                    onClick={() => handleRemoveCompany(index)}
                  >
                    Del
                  </button>
                )}
              </div>
            </div>
          ))}

          <button className="bg-teal-600 font-latoBold text-lg text-white py-5 mt-6 rounded-lg w-full">
            CREATE RESUME
          </button>
        </form>
      </div>
    </main>
  );
};

export default Home;
